	public boolean zapiszEdycjeInformacji(String imie, String nazwisko, String email, String numer_telefonu,
			String login, String haslo, String rodzaj_konta) {
		if (spbd.zapiszEdycjeInformacji(imie, nazwisko, email, numer_telefonu, login, haslo, rodzaj_konta))
			return true;

		return false;
	}

	public boolean usunUzytkownikaPracownika(String imie, String nazwisko, String email) {
		if (spbd.usunUzytkownikaPracownika(imie, nazwisko, email))
			return true;
		return false;
	}

	public boolean zapiszEdycjeWarsztat(String adres, String ilosc_stanowisk, String godzina_zamkniecia,
			String nrTelefonu, String email, String godzina_otwarcia, String miasto, String id) {
		
		if (spbd.zapiszEdycjeWarsztatu(adres, ilosc_stanowisk, godzina_zamkniecia, nrTelefonu, email,
				godzina_otwarcia, miasto, id))
			return true;

		return false;
	}
