	public boolean zapiszEdycjeWarsztat(String adres, String ilosc_stanowisk, String godzina_zamkniecia,
			String nrTelefonu, String email, String godzina_otwarcia, String miasto, String id) {

		if (spbd.zapiszEdycjeWarsztatu(adres, ilosc_stanowisk, godzina_zamkniecia, nrTelefonu, email, godzina_otwarcia,
				miasto, id))
			return true;

		return false;
	}

	public boolean usunUzytkownikaPracownika(String imie, String nazwisko, String email, String rodzaj_konta, String login) {
		if (rodzaj_konta.equals("Mechanik")) {
			if (spbd.usunMechanik(imie, nazwisko, email, login))
				return true;
			return false;
		} else {
			if (spbd.usunUzytkownikaPracownika(imie, nazwisko, email))
				return true;
			return false;
		}
	}

	
	
	
	public boolean usunWarszat(String adres, String miasto, String id_warszatatu) {
		if (spbd.usunWarsztat(adres, miasto, id_warszatatu))
			return true;
		return false;
	}
